mapper.select:
SELECT 
	UserId,
	UserName,
	CheckInTime,
	CheckState,
	IsCancel,
	CheckOutTime 
FROM userchecks


		and UserName like "%"#{userChecksCondition.userName}"%"


		and CheckInTime <  #{userChecksCondition.checkInTimeBigin} 


		and CheckInTime > #{userChecksCondition.checkInTimeEnd} 


		and CheckState = #{userChecksCondition.checkState}


limit #{page},#{rows}

SELECT 
	count(UserId) 
FROM userchecks


		and UserName like "%"#{userChecksCondition.userName}"%"


		and CheckInTime <  #{userChecksCondition.checkInTimeBigin} 


		and CheckInTime > #{userChecksCondition.checkInTimeEnd} 


		and CheckState = #{userChecksCondition.checkState}
